import { strict as assert } from "assert";
import { task } from "hardhat/config";
import { Addresses, loadAddressFile, newAddresses, saveAddressFile } from "./address_file";
import type { FundAddresses } from "./deploy_fund";

export interface CurveAddresses extends Addresses {
    crv: string;
    minter: string;
    factory: string;
    pool: string;
    token: string;
    gauge: string;
}

export const CRV_ABI = [
    "constructor(string name, string symbol, uint256 decimals)",
    "function admin() view returns (address)",
    "function minter() view returns (address)",
    "function set_minter(address minter)",
];
export const FACTORY_ABI = [
    "constructor(address fee_receiver, address pool_implementation, address token_implementation, address gauge_implementation, address weth)",
    "function pool_list(uint256 index) view returns (address)",
    "function get_gauge(address pool) view returns (address)",
    "function pool_implementation() view returns (address)",
    "function token_implementation() view returns (address)",
    "function gauge_implementation() view returns (address)",
    "function deploy_pool(string _name, string _symbol, address[2] coins, uint256 A, uint256 gamma, uint256 mid_fee, uint256 out_fee, uint256 allowed_extra_profit, uint256 fee_gamma, uint256 adjustment_step, uint256 admin_fee, uint256 ma_half_time, uint256 initial_price)",
    "function deploy_gauge(address pool) returns (address)",
];
export const POOL_ABI = [
    "constructor(address weth)",
    "function token() view returns (address)",
    "function add_liquidity(uint256[2] amounts, uint256 min_mint_amount, bool use_eth) payable returns (uint256)",
    "function exchange(uint256 i, uint256 j, uint256 dx, uint256 min_dy) returns (uint256)",
    "function exchange_underlying(uint256 i, uint256 j, uint256 dx, uint256 min_dy) payable returns (uint256)",
];
export const GAUGE_ABI = [
    "constructor()",
    "function totalSupply() view returns (uint256)",
    "function balanceOf(address addr) view returns (uint256)",
    "function rewards_receiver(address addr) view returns (address)",
    "function deposit(uint256 value)",
    "function withdraw(uint256 value)",
];
const GAUGE_CONTROLLER_ABI = [
    "function checkpoint_gauge(address)",
    "function gauge_relative_weight(address, uint256) view returns(uint256)",
    "function gauge_types(address) view returns(int128)",
];
export const MINTER_ABI = [
    "constructor(address token, address controller)",
    "function token() view returns (address)",
    "function controller() view returns (address)",
    "function minted(address user, address gauge) view returns (uint256)",
    "function mint(address gauge_addr)",
];
const VEBOOST_PROXY_ABI = ["function adjusted_balance_of(address account) view returns (uint256)"];
const CRV_VOTING_ESCROW_ABI = [
    "function totalSupply() view returns (uint256)",
    "function balanceOf(address addr) view returns (uint256)",
    "function user_point_epoch(address addr) view returns (uint256)",
    "function user_point_history__ts(address addr, uint256 epoch) view returns (uint256)",
];

const enum Bytecode {
    // https://etherscan.io/address/0xD533a949740bb3306d119CC777fa900bA034cd52
    CRV = "",
    // https://etherscan.io/address/0xa85461afc2deec01bda23b5cd267d51f765fba10
    POOL_IMPLEMENTATION = "",
    // https://etherscan.io/address/0xc08550a4cc5333f40e593ecc4c4724808085d304
    TOKEN_IMPLEMENTATION = "",
    // https://etherscan.io/address/0xdc892358d55d5ae1ec47a531130d62151eba36e5
    GAUGE_IMPLEMENTATION = "_MINTER__CRV_VOTING_ESCROW_XXXXXXXXXXXXXXXXX5afa610c79573d600060003e3d6000fd5b601f3d1115612c0457610360516103e05260206103a060446103bc73ADDR_CRV_VOTING_ESCROW_XXXXXXXXXXXXXXXXX5afa610cba573d600060003e3d6000fd5b601f3d1115612c04576103a0516103405260016103005160a052608052604060802054610360526370a082316103c052610300516103e05260206103c060246103dc73ADDR_CRV_VOTING_ESCROW__MINTER__CRV_XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX5af16118fe573d600060003e3d6000fd5b601f3d1115612c04576101a05160d81b632c4e722e6101e05260206101e060046101fc73ADDR_CRV__CRV_XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX5afa611f6c573d600060003e3d6000fd5b601f3d1115612c04576101e0516101c05263b26b238e6101e05260206101e060046101fc600073ADDR_CRV_XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX5af1611fb8573d600060003e3d6000fd5b601f3d1115612c04576101e05160d81b6101c0518181830110612c045780820190509050600f555b600e5415611ff55760006101805260006101c0525b6101205142111561232f576016546101e05263615e523761020052306102205273ADDR_CONTROLLER_XXXXXXXXXXXXXXXXXXXXXXXX3b15612c045760006000602461021c600073ADDR_CONTROLLER__CONTROLLER__VEBOOST_XXXXXXXXXXXXXXXXXXXXXXXXXXX5afa6128c0573d600060003e3d6000fd5b601f3d1115612c045761016051610140526318160ddd610180526020610180600461019c73ADDR_CRV_VOTING_ESCROW_",
    // https://etherscan.io/address/0xF18056Bbd320E96A48e3Fbf8bC061322531aac99
    FACTORY = "",
    // https://etherscan.io/address/0xd061D61a4d941c39E5453435B6345Dc261C2fcE0
    MINTER = "",
}

task("dev_deploy_curve", "Deploy the Curve factory and a pool for QUEEN")
    .addParam("underlyingSymbol", "Underlying token symbol of the fund")
    .setAction(async function (args, hre) {
        const { ethers, waffle } = hre;
        const { parseEther, parseUnits } = ethers.utils;
        const { deployMockContract } = waffle;
        const [deployer] = await ethers.getSigners();

        const underlyingSymbol: string = args.underlyingSymbol;
        assert.match(underlyingSymbol, /^[a-zA-Z]+$/, "Invalid symbol");
        const fundAddresses = loadAddressFile<FundAddresses>(
            hre,
            `fund_${underlyingSymbol.toLowerCase()}`
        );

        const wrappedToken = await ethers.getContractAt(
            "MockWrappedToken",
            fundAddresses.underlying
        );
        const shareQ = await ethers.getContractAt("ERC20", fundAddresses.shareQ);

        const Crv = await ethers.getContractFactory(CRV_ABI, Bytecode.CRV);
        const crv = await Crv.deploy("Curve DAO Token", "CRV", 18);
        console.log(`Crv: ${crv.address}`);
        const PoolImplementation = await ethers.getContractFactory(
            POOL_ABI,
            Bytecode.POOL_IMPLEMENTATION
        );
        const poolImplementation = await PoolImplementation.deploy(wrappedToken.address);
        const TokenImplementation = await ethers.getContractFactory(
            ["constructor()"],
            Bytecode.TOKEN_IMPLEMENTATION
        );
        const tokenImplementation = await TokenImplementation.deploy();

        const gaugeController = await deployMockContract(deployer, GAUGE_CONTROLLER_ABI);
        await gaugeController.mock.checkpoint_gauge.returns();
        await gaugeController.mock.gauge_relative_weight.returns(parseEther("1"));
        await gaugeController.mock.gauge_types.returns(1);
        const Minter = await ethers.getContractFactory(MINTER_ABI, Bytecode.MINTER);
        const minter = await Minter.deploy(crv.address, gaugeController.address);
        console.log(`Minter: ${minter.address}`);
        await crv.set_minter(minter.address);
        const veboostProxy = await deployMockContract(deployer, VEBOOST_PROXY_ABI);
        await veboostProxy.mock.adjusted_balance_of.returns(0);
        const crvVotingEscrow = await deployMockContract(deployer, CRV_VOTING_ESCROW_ABI);
        await crvVotingEscrow.mock.totalSupply.returns(0);
        await crvVotingEscrow.mock.balanceOf.returns(0);
        await crvVotingEscrow.mock.user_point_epoch.returns(0);
        await crvVotingEscrow.mock.user_point_history__ts.returns(0);

        const gaugeBytecode = Bytecode.GAUGE_IMPLEMENTATION.replace(
            /ADDR_CRV_XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX/g,
            crv.address.slice(2).toLowerCase()
        )
            .replace(
                /ADDR_CONTROLLER_XXXXXXXXXXXXXXXXXXXXXXXX/g,
                gaugeController.address.slice(2).toLowerCase()
            )
            .replace(
                /ADDR_MINTER_XXXXXXXXXXXXXXXXXXXXXXXXXXXX/g,
                minter.address.slice(2).toLowerCase()
            )
            .replace(
                /ADDR_VEBOOST_XXXXXXXXXXXXXXXXXXXXXXXXXXX/g,
                veboostProxy.address.slice(2).toLowerCase()
            )
            .replace(
                /ADDR_CRV_VOTING_ESCROW_XXXXXXXXXXXXXXXXX/g,
                crvVotingEscrow.address.slice(2).toLowerCase()
            );
        const GaugeImplementation = await ethers.getContractFactory(GAUGE_ABI, gaugeBytecode);
        const gaugeImplementation = await GaugeImplementation.deploy();
        const Factory = await ethers.getContractFactory(FACTORY_ABI, Bytecode.FACTORY);
        const factory = await Factory.deploy(
            deployer.address,
            poolImplementation.address,
            tokenImplementation.address,
            gaugeImplementation.address,
            wrappedToken.address
        );
        console.log(`Factory: ${factory.address}`);

        await factory.deploy_pool(
            "qETH",
            "qETH-ETH",
            [wrappedToken.address, shareQ.address],
            200000000, // A
            parseEther("0.01"), // gamma
            parseUnits("0.05", 8), // mid_fee
            parseUnits("0.45", 8), // out_fee
            parseEther("0.00000001"), // allowed_extra_profit
            parseEther("0.005"), // fee_gamma
            parseEther("0.0000055"), // adjustment_step
            parseUnits("50", 8), // admin_fee
            600, // ma_half_time
            parseEther("1") // initial_price
        );
        const pool = await ethers.getContractAt(POOL_ABI, await factory.pool_list(0));
        console.log(`Pool: ${pool.address}`);
        const token = await ethers.getContractAt("ERC20", await pool.token());
        console.log(`Token: ${token.address}`);

        await factory.deploy_gauge(pool.address);
        const gaugeAddress = await factory.get_gauge(pool.address);
        console.log(`Gauge: ${gaugeAddress}`);

        const addresses: CurveAddresses = {
            ...newAddresses(hre),
            crv: crv.address,
            minter: minter.address,
            factory: factory.address,
            pool: pool.address,
            token: token.address,
            gauge: gaugeAddress,
        };
        saveAddressFile(hre, `dev_curve`, addresses);
    });
